const storeSession = (value) => {
    sessionStorage.setItem('session', value);
};

const getSession = () => {
    return sessionStorage.getItem('session');
};

const removeSession = (value) => {
    sessionStorage.removeItem(value);
};

export { storeSession, getSession, removeSession };
