import React, { useState, useEffect } from 'react';
import { Box, Button, Alert, TextField, InputAdornment, IconButton, CircularProgress } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import { useUserLoginMutation } from '../services/authApi';
import {  getSession, storeSession} from '../services/sessionStorage';

const UserLogin = () => {

  const [error, setError] = useState({
    status: false,
    msg: '',
    type: '',
  });

const [session, setSession] = useState(getSession);

  const [userLogin, { isLoading }] = useUserLoginMutation()
  const navigate = useNavigate();

  const [captcha, setCaptcha] = useState('');
  const [enteredCaptcha, setEnteredCaptcha] = useState('');

  const emailRegex = /^\S+@(?!invalid\.com)([a-zA-Z0-9-]+\.)+[a-zA-Z]{2,6}$/;
  const passwordRegex = /^(?=.*[!@#$%^&*])(?=.*[a-zA-Z0-9]).{6,}$/;

  const [showPassword, setShowPassword] = useState(false);
  const handlePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const generateCaptcha = () => {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let code = '';
    for (let i = 0; i < 6; i++) {
      code += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    setCaptcha(code);
  };
  useEffect(() => {
    generateCaptcha();
  }, []);

  const handleErrorTimeout = (message, type) => {
    setError({ status: true, msg: message, type: type });
    setTimeout(() => {
      setError({ status: false, msg: '', type: '' });
    }, 5000); 
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (enteredCaptcha === captcha) {
      const data = new FormData(e.currentTarget);
      const actualData = {
        email: data.get('email'),
        password: data.get('password'),
      };

      if (actualData.email && actualData.password !== null) {

        if (!emailRegex.test(actualData.email)) {
          handleErrorTimeout( "Please enter a valid email address",  'error' );
          return;
        }
        if (!passwordRegex.test(actualData.password)) {
          handleErrorTimeout("Password must be at least 6 characters long and contain at least one special character.",'error' );
          return;
        }

        const res = await userLogin(actualData);
        console.log(res);
        if (res.data.status === 'success') { if (res.session) {
          setSession(session);
          storeSession(session)
          setTimeout(() => {
            navigate('/verifylogin');
            window.location.reload();
          }, 3000);
        } else {
          console.error('session is not defined in the response:', res);
          setError({ status: true, msg: 'Error during registration: authid is missing', type: 'error' });
        } 
      }

        if (res.data.status === 'failed') {
          console.error('error during login', error);
          handleErrorTimeout( res.data.message,'error' );
        }
      }
      else {
        handleErrorTimeout('All fields are required','error' );
      }
    }
    else {
      handleErrorTimeout('Captcha code does not match', 'error' );
      generateCaptcha(); 
    }
  };

  return <>
    <h2 style={{ textAlign: 'center', color: 'navy' }}>Welcome!!!</h2>
    <Box component='form' noValidate sx={{
      mt: 1, p: 1, maxWidth: '400px', margin: 'auto'
    }} id='login-form' onSubmit={handleSubmit}>
      <TextField
        margin='normal' required fullWidth id='email' name='email' label='Email Address' />
      <TextField
        margin='normal' required fullWidth id='password' name='password' type={showPassword ? 'text' : 'password'} label='Password' InputProps={{
          endAdornment: (
            <InputAdornment position='end'>
              <IconButton onClick={handlePasswordVisibility} edge='end'>
                {showPassword ? <VisibilityIcon /> : <VisibilityOffIcon />}
              </IconButton>
            </InputAdornment>
          ),
        }}
      />
      <TextField
          margin='normal' required fullWidth id='captcha' name='captcha' type='text' label='Captcha' value={enteredCaptcha}
          onChange={(e) => setEnteredCaptcha(e.target.value)} />
        <div style={{ textAlign: 'center', margin: '10px 0' }}>
          <button  variant="contained" sx={{ mt: 1, px: 5, backgroundColor: 'white', color: 'navy', fontWeight: '550' }} onClick={generateCaptcha}>Regenerate CAPTCHA </button>{captcha}
        </div>
      <Box sx={{ textAlign: 'center', mt: 1, color: 'white' }}>
        {isLoading ? <CircularProgress /> :
          <Button type='submit' variant='contained' sx={{ mt: 3, px: 5, backgroundColor: 'white', color: 'navy', fontWeight: '550' }}>
            Login
          </Button>}
        <br /><br />
      </Box>
      {error.status && <Alert severity={error.type}>{error.msg}</Alert>}
    </Box>
  </>
};

export default UserLogin;
