import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react';

export const authApi = createApi({
  reducerPath: 'authApi',
  baseQuery: fetchBaseQuery({ baseUrl: 'http://127.0.0.1:4000/api/user/'}),

  endpoints: (builder) => ({
    userReg: builder.mutation({
      query: (user) => ({
        url: 'reg',
        method: 'POST',
        body: user,
        headers: {
          'Content-type': 'application/json',
        },
      }),
    }),
    userLogin: builder.mutation({
      query: (user) => ({
        url: 'login',
        method: 'POST',
        body: user,
        headers: {
          'Content-type': 'application/json',
        },
      }),
    }),

    getLoggedUser: builder.query({
      query: (sessionID) => ({
        url: `/loggedUser`,
        method: 'GET',
        headers: {
          'Cookie': `sessionID=${sessionID}`, // Example: Set the session ID in the cookie
        },
      }),
    }),
  }),
});

export const {useUserRegMutation,useUserLoginMutation,useGetLoggedUserQuery} = authApi;
