      value = isPlainObject(value) ? Object.keys(value).sort().reduce<any>((acc, key) => {
        acc[key] = (value as any)[key];
        return acc;
      }, {}) : value;
      return value;
    });
    if (isPlainObject(queryArgs)) {
      cache?.set(queryArgs, stringified);
    }
    serialized = stringified;
  }
  return `${endpointName}(${serialized})`;
};
export type SerializeQueryArgs<QueryArgs, ReturnType = string> = (_: {
  queryArgs: QueryArgs;
  endpointDefinition: EndpointDefinition<any, any, any, any>;
  endpointName: string;
}) => ReturnType;
export type InternalSerializeQueryArgs = (_: {
  queryArgs: any;
  endpointDefinition: EndpointDefinition<any, any, any, any>;
  endpointName: string;
}) => QueryCacheKey;

import { useEffect, useRef, useMemo } from 'react';
import type { SerializeQueryArgs } from '@reduxjs/toolkit/query';
